            setSearchSuggestion('instagrom');
        } else if (lowerValue && 'martin.kil'.startsWith(lowerValue)) {
            setSearchSuggestion('martin.kil');
        } else if (lowerValue && 'onlyfon'.startsWith(lowerValue)) {
            setSearchSuggestion('onlyfon');
